data FractalType = TyMandelbrot | TyJulia deriving(Show, Eq)
data FractalFile = MF Int Int Int Double Double Double Double [Int]
                 | JF Int Int Int Double Double Double Double Double Double [Int]
                    deriving(Show, Eq)



-- build Fractal file
buildFF :: FractalType -> String -> FractalFile
buildFF TyMandelbrot s =
  let (_:maxiter:r:c:rl:cl:rh:ci:cR:cI:valsStr) = words s
      vals = map (\x->read x :: Int) valsStr
  in  MF(read maxiter) (read r)(read c)(read rl)(read cl)(read rh)(read ci) vals
buildFF TyJulia s =
  let (_:maxiter:r:c:rl:cl:rh:ci:cR:cI:valsStr) = words s
      vals = map (\x->read x :: Int) valsStr
  in  JF(read maxiter) (read r)(read c)(read rl)(read cl)(read rh)(read ci)(read cR)(read cI) vals
      


assertEqual :: (Eq a, Show a) => String -> a -> a -> Assertion
assertEqual preface expected actual =
  unless (actual == expected) (assertFailure msg)
 where msg = (if null preface then "" else preface ++ "\n")


hGetContents' hdl = do e <- hIsEOF hdl
                       if e then return []
                            else do c <- hGetChar hdl
                                    cs <- hGetContents' hdl
                                    return (c:cs)
 
readFile' fn = do hdl <- openFile fn ReadMode
                  xs <- hGetContents' hdl
                  hClose hdl
                  return xs

-- s: message. a: expected. b: calculated.
tc s a b = TestCase $ assertEqual s a b
tcio s mio answer = TestLabel s $ TestCase $ do
    got <- mio
    assertEqual s answer got

