toFileGetBackM :: Mandelbrot -> Int -> Test
toFileGetBackM m n = TestLabel ("mandel toFile "++show n) $ TestCase $ do
  let filename = "outputFileGoesHere.txt"
  toFile m filename
  got <- readFile' filename
  assertEqual ("mandel toFile "++show n)
              (buildFF TyMandelbrot (fullStrM m n))
              (buildFF TyMandelbrot got)
  
  
toFileGetBackJ :: Julia -> Int -> Test
toFileGetBackJ j n = TestLabel ("julia toFile "++show n) $ TestCase $ do
  let filename = "outputFileGoesHere.txt"
  toFile j filename
  got <- readFile' filename
  assertEqual ("julia toFile "++show n)
              (buildFF TyJulia (fullStrJ j n))
              (buildFF TyJulia got)
  
  

