-- Some definitions that were easier to compare with my own *Soln version than paste in the answers.
buildGridSoln :: Int -> Int -> Complex -> Complex -> [[Complex]]
buildGridSoln nrows ncols (C lowR lowI) (C hiR hiI) = let row'  = [ C $ lowR + (hiR-lowR)*(fromIntegral x/(fromIntegral (ncols-1))) | x <- [0..ncols-1] ]
                                                          colis = [     lowI + (hiI-lowI)*(fromIntegral x/(fromIntegral (nrows-1))) | x <- [nrows-1,nrows-2..0] ]
                                                      in map (\(fs,i) -> zipWith ($) fs (repeat i)) $ zip (repeat row') colis

showGridSoln :: (Show a) => [[a]] -> String
showGridSoln xss = let padnum width n =  (take (width-length (show n)) (repeat ' '))++show n
                       ss = (map (map (padnum (maxx xss))) xss) :: [[String]]
                   in foldr (\s z-> s++"\n"++z) "" $ map (foldr (\s z -> s++" "++z) "") $ ss
  where maxx :: (Show a) => [[a]] -> Int
        maxx xss = foldr1 max $ map (length . show) $ concat xss

