gridma (M iters r c lowC hiC) = buildGrid r c lowC hiC
gridme (M iters r c lowC hiC) = buildGridSoln r c lowC hiC


showM (M m r c (C w x) (C y z)) = "mandelbrot "++ (intercalate " " $ map show [m,r,c] ++ map show [w,x,y,z,0.0,0.0])
showJ (J m r c (C w x) (C y z) (C s t)) = "julia " ++ (intercalate " " $ map show [m,r,c] ++ map show [w,x,y,z,s,t])

strPrefixM m = showM m
strPrefixJ j = showJ j

